  @ReactMethod
  public void CreateChannel(boolean withVideo, Promise promise) {
    Log.d(TAG, "===== CreateChannel: withVideo = " + withVideo);
    IRtcChannel rtcChannel = ValleyRtcAPI.CreateChannel(withVideo, reactContext);
    if (rtcChannel != null) {
      rtcChannel.RegisterRtcSink(new RtcSinkImpl(), g_ChannelIndex);
      rtcChannelMap.put(g_ChannelIndex, rtcChannel);
      promise.resolve(g_ChannelIndex);
      g_ChannelIndex++;
    }
    else {
      // IRtcChannel.ERR_NOT_INITIALIZE
      promise.reject("-5", "-5");
    }
  }
