  @ReactMethod
  public void ChannelGetUser(int index, String userid, Promise promise) {
    Log.d(TAG, "===== ChannelGetUser: userid = " + userid + ", index = " + index);
    IRtcChannel rtcChannel = rtcChannelMap.get(index);
    if (rtcChannel != null) {
      objUser u = rtcChannel.GetUser(userid);
      if (u == null) {
        promise.reject("-5", "-5");
      }
      else {
        WritableMap params = Arguments.createMap();
        params.putString("userid", u.getUserid());
        promise.resolve(params);
      }
    }
    else {
      // IRtcChannel.ERR_NOT_INITIALIZE
      promise.reject("-5", "-5");
    }
  }

  @ReactMethod
  public void ChannelGetUserList(int index, Promise promise) {
    Log.d(TAG, "===== ChannelGetUserList: " + ", index = " + index);
    IRtcChannel rtcChannel = rtcChannelMap.get(index);
    if (rtcChannel != null) {
      objUserList u = rtcChannel.GetUserList();
      if (u == null) {
        promise.reject("-5", "-5");
      }
      else {
        WritableMap params = Arguments.createMap();
        params.putInt("count", u.size());

        WritableArray paramList = Arguments.createArray();
        for (int i = 0; i < u.size(); i++) {
          paramList.pushString(u.item(i).getUserid());
        }

        params.putArray("user_id_list", paramList);
        promise.resolve(params);
      }
      promise.resolve(null);
    }
    else {
      // IRtcChannel.ERR_NOT_INITIALIZE
      promise.reject("-5", "-5");
    }
  }
