package com.reactlibrary;

import android.util.Log;
import android.view.SurfaceView;

import com.facebook.react.uimanager.SimpleViewManager;
import com.facebook.react.uimanager.ThemedReactContext;
import com.facebook.react.uimanager.annotations.ReactProp;

public class RNValleyRtcApiVideoViewManager extends SimpleViewManager<SurfaceView> {
    private static final String VIDEO_VIEW_MANAGER_NAME = "RNValleyRtcApiVideoView";
    private static final String TAG = "RNValleyRtcApiVideoViewManager";
    private ThemedReactContext mContext;
    private int index = -1;
    private Boolean remove = true;
    private Boolean local = true;
    private Boolean reload = false;
    private String userId = "";

    @Override
    public String getName() {
        return VIDEO_VIEW_MANAGER_NAME;
    }

    @Override
    protected SurfaceView createViewInstance(ThemedReactContext reactContext) {
        this.mContext = reactContext;
        SurfaceView videoView = new SurfaceView(reactContext);
        return videoView;
    }

    @ReactProp(name = "index")
    public void setIndex(final SurfaceView view, int index) {
        this.index = index;
    }

    @ReactProp(name = "userId")
    public void setUserId(final SurfaceView view, String userId) {
        this.userId = userId;
    }

    @ReactProp(name = "remove")
    public void setRemove(final SurfaceView view, Boolean remove) {
        this.remove = remove;
    }

    @ReactProp(name = "local")
    public void setLocal(final SurfaceView view, Boolean local) {
        this.local = local;
    }

    @ReactProp(name = "reload")
    public void setReload(final SurfaceView view, Boolean reload) {
        this.reload = reload;
        Log.d(TAG, "===== setReload, reload = " + this.reload + ", index = " + this.index + ", remove = " + this.remove +
            ", local = " + this.local + ", userId = " + this.userId);

        if (this.reload) {
            if (this.index != -1) {
                if (this.remove) {
                    if (this.local) {
                        RNValleyRtcApiSingleton.getInstance().ChannelRemoveLocalVideo(this.index);
                    }
                    else {
                        if (this.userId != "") {
                            RNValleyRtcApiSingleton.getInstance().ChannelRemoveUserVideo(this.userId, this.index);
                        }
                    }
                }
                else {
                    if (this.local) {
                        RNValleyRtcApiSingleton.getInstance().ChannelSetLocalVideo(view, this.index);
                    }
                    else {
                        if (this.userId != "") {
                            RNValleyRtcApiSingleton.getInstance().ChannelSetUserVideo(view, this.userId, this.index);
                        }
                    }
                }
            }
        }
    }
}
