- (void)onTimer:(NSTimer*)sender {
    if (haveListener && rtcChannel) {
	objBaseEvent *event = [rtcChannel GetEvent];
	if (event) {
	    if ([event getEventID] > RTC_EVTID_NTF_FROM) {
		// TODO: notify
	    }
	    else {
		// TODO: response
	    }
	    [self sendEventWithName:@"geolocationDidChange" body:@{@"name":@"123213"}];
	}
	[rtcChannel ReleaseEvent:event];
    }
}

- (void)startObserving {
    RCTLogInfo(@"=======  startObserving");
    haveListener = YES;
    if (!timer) {
        timer = [ NSTimer  scheduledTimerWithTimeInterval: 0.5
                                               target: self
                                               selector: @selector(onTimer:)
                                               userInfo:nil
                                               repeats: YES ];
    }
}

- (void)stopObserving {
    RCTLogInfo(@"=======  stopObserving");
    haveListener = NO;
    if (timer) {
        [timer invalidate];
        timer = nil;
    }
}

