#import <React/UIView+React.h>

@implementation RNValleyRtcApiVideoView

- (instancetype)init {
    self = [super init];
    if (self) {
        _index = -1;
        _remove = true;
        _local = true;
        _reload = false;
        _userId = nil;
        NSLog(@"=======  RNValleyRtcApiVideoView::init");
    }
    return self;
}

