- (BOOL)hasKey:(NSDictionary*)dict key:(NSString*)key {
    if(![dict objectForKey:key]){
        return NO;
    }
    id obj = [dict objectForKey:key];
    return ![obj isEqual:[NSNull null]];
}

- (void)setVideoInfo:(NSDictionary*)videoInfo {
//    NSLog(@"=======  videoInfo, index = %d, userid = %@, reload = %d", videoInfo.index, videoInfo.userId, videoInfo.reload);
    NSLog(@"=======  videoInfo, index = %@, userid = %@, reload = %@, local = %@, remove = %@", videoInfo[@"index"], videoInfo[@"userId"], videoInfo[@"reload"], videoInfo[@"local"], videoInfo[@"remove"]);
    
    if ([self hasKey:videoInfo key:@"index"]) {
        _index = [videoInfo[@"index"] intValue];
    }
    
    if ([self hasKey:videoInfo key:@"userId"]) {
        _userId = videoInfo[@"userId"];
    }
    
    if ([self hasKey:videoInfo key:@"local"]) {
        _local = [videoInfo[@"local"] boolValue];
    }
    
    if ([self hasKey:videoInfo key:@"remove"]) {
        _remove = [videoInfo[@"remove"] boolValue];
    }
    
    if ([self hasKey:videoInfo key:@"reload"]) {
        [self setReload:[videoInfo[@"reload"] boolValue]];
    }
}

- (void)setIndex:(int)index {
    NSLog(@"=======  setIndex, index = %d", index);
    _index = index;
}

- (void)setRemove:(BOOL)remove {
    NSLog(@"=======  setRemove, remove = %d", remove);
    _remove = remove;
}

- (void)setLocal:(BOOL)local {
    NSLog(@"=======  setLocal, local = %d", local);
    _local = local;
}

- (void)setUserId:(NSString *)userId {
    NSLog(@"=======  setUserId, userId = %@", userId);
    _userId = userId;
}

- (void)setReload:(BOOL)reload {
    _reload = reload;
    NSLog(@"=======  setReload entry, reload = %d, index = %ld, remove = %d, local = %d, userid = %@", _reload ,(long)_index, _remove, _local, _userId);
    if (_reload) {
        int channelIndex = _index;
        if (channelIndex != -1) {
            if (_remove) {
                if (_local) {
                    [[RNValleyRtcApiInternal GetInstance] RemoveLocalVideo:channelIndex];
                }
                else {
                    if (_userId != nil && ![_userId isEqual: @""]) {
                        [[RNValleyRtcApiInternal GetInstance] RemoveUserVideo:channelIndex userid:_userId];
                    }
                }
            }
            else {
#if TARGET_OS_SIMULATOR
                xVideoCanvas *canvas = nil;
#else
                xVideoCanvas *canvas = [xVideoCanvas new];
                canvas.view = self;
                canvas.renderMode = RENDER_TYPE_ADAPTIVE;
                canvas.mirrorMode = MirrorMode_All;
#endif
                if (_local) {
                    [[RNValleyRtcApiInternal GetInstance] SetLocalVideo:channelIndex hVideo:canvas];
                }
                else {
                    if (_userId != nil && ![_userId isEqual: @""]) {
                        [[RNValleyRtcApiInternal GetInstance] SetUserVideo:channelIndex userid:_userId hVideo:canvas];
                    }
                }
            }
        }
    }
    NSLog(@"=======  setReload exit");
}

@end
